import express from 'express';
import { services } from 'platform-a/platform-a.node.js';

const service = {
  name: 'people',
  run: async (port) => {
    const server = express();
    server.use((_, res) => res.send('Hello, people!'));
    server.listen(port);
    console.log('People service running on port', port);
    return server;
  }
};

services.add({
  server: service,
  port: 3000
});
