import express from 'express';
import { Service, services } from 'platform-a/platform-a.node';

const service: Service = {
  name: 'people',
  run: async (port: number) => {
    const server = express();
    server.use((_, res) => res.send('Hello, people!'));
    server.listen(port);
    return server;
  }
};

services.add({
  server: service,
  port: 3000
});
